/* eslint require-await: "error" */

const state = {
  fileIcons: {
    drive: "far fa-hard-drive",
    folder: "far fa-folder-closed",
    js: "fab fa-js",
    mp4: "fas fa-file-video",
    mkv: "fas fa-file-video",
    avi: "fas fa-file-video",
    wmv: "fas fa-file-video",
    mov: "fas fa-file-video",
    mxf: "fas fa-file-video",
    other: "far fa-file",
  },
  tableHeads: [
    {
      name: "NAME",
      width: "55%",
    },
    {
      name: "SIZE",
      width: "25%",
    },
    {
      name: "FILE COUNT",
      width: "15%",
    },
  ],
  SelectedTableHeads: [
    {
      name: "NAME",
    },
    {
      name: "SIZE",
    },
  ],
  selectedItems: {
    folders: {
      path: "",
      files: [],
    },
  },
  isInitialLoading: true,
};

// getters
const getters = {};

const mutations = {
  CHANGELOADINGSTATUS: (state, value) => {
    state.isInitialLoading = value;
  },
};

export default {
  namespaced: true,
  state,
  getters,
  mutations,
};
